import Ma315_BE_lib as libimport numpy as npdata_brut = np.genfromtxt('iris.csv', dtype=str, delimiter=',')data = data_brut[1:, :-1].astype('float')label_colonne = data_brut[0,: -1]label_ligne = data_brut[1:, -1]lib.ACP2D(data, label_colonne, label_ligne)lib.Cercle_corr2D(data, label_colonne)#%%data_brut = np.genfromtxt('howellmod.csv', dtype=str, delimiter=',')data = data_brut[1:, 1:].astype('float')label_colonne = data_brut[0, 1:]label_ligne = data_brut[1:, 0]lib.ACP2D(data, label_colonne, label_ligne, (1, 2))lib.Cercle_corr2D(data, label_colonne, (1, 2))#%%data_brut = np.genfromtxt('pizzamod.csv', dtype=str, delimiter=',')data = data_brut[1:, 1:].astype('float')label_colonne = data_brut[0, 1:]label_ligne = data_brut[1:, 0]lib.ACP2D(data, label_colonne, label_ligne)lib.Cercle_corr2D(data, label_colonne)#%%data_brut = np.genfromtxt('gini-dataset.csv', dtype=str, delimiter=',')data_brut[data_brut == ''] = '0'data = data_brut[1:, 1:].astype('float')label_colonne = data_brut[0, 1:]label_ligne = data_brut[1:, 0]lib.ACP2D(data, label_colonne, label_ligne)lib.Cercle_corr2D(data, label_colonne)#%%data_brut = np.genfromtxt('mcu-dataset.csv', dtype=str, delimiter=';')data_brut = np.char.replace(data_brut, ',', '')data_brut = np.char.replace(data_brut, '$', '')data = data_brut[1:, 2:].astype('float')label_colonne = data_brut[0, 2:]label_ligne = data_brut[1:, 2]lib.ACP2D(data, label_colonne, label_ligne)lib.Cercle_corr2D(data, label_colonne)